import React, { useState, useEffect } from "react";
import "../Styles/InstaFeed.css";

const InstaFeed = () => {
  // Estado para almacenar las publicaciones de Instagram
  const [posts, setPosts] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  // ID de usuario y token de acceso
  const userId = "17841461036239831"; // El ID de usuario de Instagram
  const accessToken = "TU_TOKEN_DE_ACCESO"; // Tu token de acceso

  // URL para obtener las publicaciones, incluyendo tipo de medio
  const url = `https://graph.instagram.com/${userId}/media?fields=id,caption,media_type,media_url,thumbnail_url,permalink,timestamp,children&access_token=${accessToken}`;

  // Efecto para obtener las publicaciones cuando el componente se monte
  useEffect(() => {
    fetch(url)
      .then((response) => response.json())
      .then((data) => {
        const filteredPosts = data.data.filter(
          (post) =>
            post.media_type === "IMAGE" ||
            post.media_type === "VIDEO" ||
            post.media_type === "CAROUSEL_ALBUM" ||
            post.media_type === "REELS"
        ); // Filtrar los Reels también
        setPosts(filteredPosts); // Almacenar las publicaciones
        setLoading(false); // Dejar de mostrar el loading
      })
      .catch((error) => {
        setError(error); // Manejo de errores
        setLoading(false); // Dejar de mostrar el loading
      });
  }, []); // Se ejecuta solo una vez al montar el componente

  if (loading) {
    return <p>Cargando publicaciones...</p>; // Mensaje de carga
  }

  if (error) {
    return <p>Error al cargar las publicaciones: {error.message}</p>; // Mensaje de error
  }

  return (
    <div className="insta-feed">
      <h2>Últimas publicaciones de Instagram</h2>
      <div className="posts">
        {posts.map((post) => (
          <div key={post.id} className="post">
            <a href={post.permalink} target="_blank" rel="noopener noreferrer">
              {post.media_type === "IMAGE" || post.media_type === "VIDEO" ? (
                <img width={400} src={post.media_url} alt={post.caption} />
              ) : post.media_type === "REELS" ? (
                <video controls>
                  <source width={400} src={post.media_url} type="video/mp4" />
                  Tu navegador no soporta el elemento de video.
                </video>
              ) : null}
            </a>
            <p>{post.caption}</p>
          </div>
        ))}
      </div>
    </div>
  );
};

export default InstaFeed;
