import React, { useState, useEffect } from "react";
import "../Styles/InstaFeed.css";

const InstaFeed = () => {
  // Estado para almacenar las publicaciones de Instagram
  const [posts, setPosts] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  // ID de usuario y token de acceso
  const userId = "17841461036239831"; // El ID de usuario de Instagram
  const accessToken =
    "IGQWRPUG9XUFR3WXhpQkRmMmtCTnpuYi1mMlRWdzNQeGFNVV9NVmlhVDQ2TnVtakFWblE1TkJzWS1zWmdsZA2F1ckczeDhmTVJOTjJ5RGoxdkI5RWJUa0pzVTNjM2R4Q0VvaEpDZAlB1cnNTd29VY0dWWC1jUTFIeE0ZD"; // Tu token de acceso

  // URL para obtener las publicaciones
  const url = `https://graph.instagram.com/${userId}/media?fields=id,caption,media_type,media_url,thumbnail_url,permalink,timestamp&access_token=${accessToken}`;

  // Efecto para obtener las publicaciones cuando el componente se monte
  useEffect(() => {
    fetch(url)
      .then((response) => response.json())
      .then((data) => {
        setPosts(data.data); // Almacenar las publicaciones
        setLoading(false); // Dejar de mostrar el loading
      })
      .catch((error) => {
        setError(error); // Manejo de errores
        setLoading(false); // Dejar de mostrar el loading
      });
  }, []); // Se ejecuta solo una vez al montar el componente

  if (loading) {
    return <p>Cargando publicaciones...</p>; // Mensaje de carga
  }

  if (error) {
    return <p>Error al cargar las publicaciones: {error.message}</p>; // Mensaje de error
  }

  return (
    <div className="insta-feed">
      <h2>Ãšltimas publicaciones de Instagram</h2>
      <div className="posts">
        {posts.map((post) => (
          <div key={post.id} className="post">
            <a href={post.permalink} target="_blank" rel="noopener noreferrer">
              <img src={post.media_url} width={400} alt={post.caption} />
            </a>
            <p>{post.caption}</p>
          </div>
        ))}
      </div>
    </div>
  );
};

export default InstaFeed;
