// Chatbot.js
import React, { useState } from "react";

const Chatbot = () => {
  const [messages, setMessages] = useState([]);
  const [userInput, setUserInput] = useState("");

  const handleInputChange = (e) => {
    setUserInput(e.target.value);
  };

  const sendMessage = async () => {
    if (!userInput.trim()) return;

    const newMessages = [...messages, { sender: "user", text: userInput }];
    setMessages(newMessages);
    setUserInput("");

    // Call to backend API or function to process the input
    const response = await getChatbotResponse(userInput);

    // Store interaction in MagicLoops (or another database) after receiving response
    await storeInteraction(userInput, response);

    setMessages([...newMessages, { sender: "bot", text: response }]);
  };

  const getChatbotResponse = async (input) => {
    // Aquí procesamos el input para ver si es precio, Messi, etc.
    if (
      input.toLowerCase().includes("btc") ||
      input.toLowerCase().includes("bitcoin")
    ) {
      return await fetchBTCPrice();
    }

    if (input.toLowerCase().includes("messi")) {
      return "¡Messi! El crack argentino. Es uno de los futbolistas más famosos del mundo. ¿Quieres saber más sobre él o prefieres algo sobre criptos?";
    }

    // Default fallback: call to Wikipedia API for general knowledge.
    return await fetchWikipediaInfo(input);
  };

  const fetchBTCPrice = async () => {
    const res = await fetch(
      "https://api.coingecko.com/api/v3/simple/price?ids=bitcoin&vs_currencies=usd"
    );
    const data = await res.json();
    return `El precio de Bitcoin es $${data.bitcoin.usd} USD.`;
  };

  const fetchWikipediaInfo = async (query) => {
    const res = await fetch(
      `https://es.wikipedia.org/w/api.php?action=query&format=json&titles=${encodeURIComponent(
        query
      )}&prop=extracts&exintro&explaintext`
    );
    const data = await res.json();
    const page = Object.values(data.query.pages)[0];
    return page.extract
      ? page.extract
      : `No pude encontrar información sobre "${query}" en Wikipedia. ¿Te interesa saber otra cosa?`;
  };

  const storeInteraction = async (userQuery, botResponse) => {
    await fetch("https://api.magicloops.com/store", {
      method: "POST",
      body: JSON.stringify({ userQuery, botResponse }),
      headers: { "Content-Type": "application/json" },
    });
  };

  return (
    <div className="chatbot-container">
      <div className="chatbot-messages">
        {messages.map((msg, index) => (
          <div key={index} className={msg.sender}>
            {msg.text}
          </div>
        ))}
      </div>
      <input
        type="text"
        value={userInput}
        onChange={handleInputChange}
        onKeyPress={(e) => (e.key === "Enter" ? sendMessage() : null)}
        placeholder="Escribe algo..."
      />
      <button onClick={sendMessage}>Enviar</button>
    </div>
  );
};

export default Chatbot;
