import { useEffect } from "react";

function SmoothScroll() {
  useEffect(() => {
    let scrollPosition = 0;
    let scrollTarget = 0;
    let isScrolling = false;

    // Función para manejar el desplazamiento
    const onScroll = () => {
      scrollTarget = window.scrollY;
      if (!isScrolling) {
        requestAnimationFrame(smoothScroll);
      }
      isScrolling = true;
    };

    // Función para suavizar el desplazamiento
    const smoothScroll = () => {
      scrollPosition += (scrollTarget - scrollPosition) * 0.1; // Ajusta el factor de suavizado aquí
      window.scrollTo(0, scrollPosition);

      if (Math.abs(scrollTarget - scrollPosition) > 0.5) {
        requestAnimationFrame(smoothScroll);
      } else {
        isScrolling = false;
      }
    };

    window.addEventListener("scroll", onScroll);

    return () => {
      window.removeEventListener("scroll", onScroll);
    };
  }, []);

  return null; // No renderiza nada, solo controla el desplazamiento
}

export default SmoothScroll;
