import React, { useState } from "react";

const VideoDownloader = () => {
  const [videoUrl, setVideoUrl] = useState("");
  const [downloadLink, setDownloadLink] = useState("");
  const [status, setStatus] = useState("");

  const handleSubmit = async (event) => {
    event.preventDefault();
    setStatus("Descargando...");

    try {
      const response = await fetch(
        `https://yt1s.com/api/convert?url=${videoUrl}`
      );
      const data = await response.json();

      if (data.success) {
        setDownloadLink(data.download_url); // Supón que la API devuelve un enlace de descarga
        setStatus("Descarga completada");
      } else {
        setStatus("Error al obtener el video");
      }
    } catch (error) {
      setStatus("Error: " + error.message);
    }
  };

  return (
    <div>
      <h1>Descargar Video de YouTube</h1>
      <form onSubmit={handleSubmit}>
        <input
          type="text"
          value={videoUrl}
          onChange={(e) => setVideoUrl(e.target.value)}
          placeholder="Introduce el link de YouTube"
        />
        <button type="submit">Descargar</button>
      </form>
      {status && <p>{status}</p>}
      {downloadLink && (
        <a href={downloadLink} download="video.mp4">
          Haz clic aquí para descargar el video
        </a>
      )}
    </div>
  );
};

export default VideoDownloader;
