import React, { useState, useEffect } from "react";
import "../Styles/InstaFeed.css";

const InstaFeed = () => {
  // Estado para almacenar las publicaciones de Instagram
  const [posts, setPosts] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  // ID de usuario y token de acceso
  const userId = "17841461036239831"; // El ID de usuario de Instagram
  const accessToken =
    "IGQWRPUG9XUFR3WXhpQkRmMmtCTnpuYi1mMlRWdzNQeGFNVV9NVmlhVDQ2TnVtakFWblE1TkJzWS1zWmdsZA2F1ckczeDhmTVJOTjJ5RGoxdkI5RWJUa0pzVTNjM2R4Q0VvaEpDZAlB1cnNTd29VY0dWWC1jUTFIeE0ZD"; // Tu token de acceso

  // URL para obtener las publicaciones, incluyendo tipo de medio
  const url = `https://graph.instagram.com/${userId}/media?fields=id,caption,media_type,media_url,thumbnail_url,permalink,timestamp,children&access_token=${accessToken}`;

  // Efecto para obtener las publicaciones cuando el componente se monte
  useEffect(() => {
    fetch(url)
      .then((response) => response.json())
      .then((data) => {
        const filteredPosts = data.data.filter(
          (post) =>
            post.media_type === "IMAGE" ||
            post.media_type === "VIDEO" ||
            post.media_type === "REELS"
        );

        // Obtener el número de likes de cada publicación
        Promise.all(
          filteredPosts.map((post) =>
            getPostLikes(post.id, accessToken).then((likes) => ({
              ...post,
              likes,
            }))
          )
        )
          .then((postsWithLikes) => {
            setPosts(postsWithLikes);
            setLoading(false);
          })
          .catch((error) => {
            setError(error);
            setLoading(false);
          });
      })
      .catch((error) => {
        setError(error);
        setLoading(false);
      });
  }, []);

  if (loading) {
    return <p>Cargando publicaciones...</p>; // Mensaje de carga
  }

  if (error) {
    return <p>Error al cargar las publicaciones: {error.message}</p>; // Mensaje de error
  }
  return (
    <div className="insta-feed">
      <div className="posts">
        {posts.map((post) => (
          <div key={post.id} className="post">
            <a href={post.permalink} target="_blank" rel="noopener noreferrer">
              {post.media_type === "IMAGE" ||
              post.media_type === "CAROUSEL_ALBUM" ? (
                <img src={post.media_url} alt={post.caption} width={200} />
              ) : post.media_type === "VIDEO" || post.media_type === "REELS" ? (
                <video
                  src={post.media_url}
                  alt={post.caption}
                  width={200}
                  loop={true} //   controls={true}
                  muted={true}
                  autoPlay={true}
                />
              ) : null}
            </a>
            <p>{post.caption}</p>
            <p>Likes: {post.likes}</p> {/* Mostrar el número de likes */}
          </div>
        ))}
      </div>
    </div>
  );
};

export default InstaFeed;
