import React, { useState, useEffect } from "react";

const CurrencyConverter = () => {
  const [amount, setAmount] = useState("");
  const [dolar, setDolar] = useState([]);

  useEffect(() => {
    fetch("https://api.bluelytics.com.ar/v2/latest")
      .then((response) => response.json())
      .then((data) => setDolar(data));
  }, []);

  const exchangeRateSell = dolar?.blue?.value_sell;
  const exchangeRateBuy = dolar?.blue?.value_buy;

  const formatNumber = (num) => {
    return num.replace(/\B(?=(\d{3})+(?!\d))/g, ".");
  };

  const handleChange = (e) => {
    let value = e.target.value.replace(/\./g, ""); // Remove existing dots
    if (!isNaN(value)) {
      value = parseInt(value).toString(); // Convert back to string without leading zeros
      setAmount(formatNumber(value));
    }
  };

  return (
    <div>
      <h2>Currency Converter</h2>
      <input
        type="text"
        value={amount}
        onChange={handleChange}
        placeholder="USD"
      />
      <p>
        {amount} USD Compra ={" "}
        {(parseInt(amount.replace(/\./g, "")) * exchangeRateBuy)
          .toFixed(0)
          .replace(/\B(?=(\d{3})+(?!\d))/g, ".")}{" "}
        ARS
        <br />
        <br />
        {amount} USD Venta ={" "}
        {(parseInt(amount.replace(/\./g, "")) * exchangeRateSell)
          .toFixed(0)
          .replace(/\B(?=(\d{3})+(?!\d))/g, ".")}{" "}
        ARS
      </p>
    </div>
  );
};

export default CurrencyConverter;
