import React, { useState, useEffect } from "react";

const Friends = () => {
  const [followingData, setFollowingData] = useState(null);
  const [followersData, setFollowersData] = useState(null);
  const [searchTerm, setSearchTerm] = useState("");
  const [sortAscending, setSortAscending] = useState(true);

  useEffect(() => {
    // Fetch both JSON files
    Promise.all([
      fetch("/following.json").then((response) => response.json()),
      fetch("/followers.json").then((response) => response.json()),
    ])
      .then(([following, followers]) => {
        console.log("Following Data:", following);
        console.log("Followers Data:", followers);
        setFollowingData(following);
        setFollowersData(followers);
      })
      .catch((error) => console.error("Error fetching the JSON data:", error));
  }, []);

  if (!followingData || !followersData) {
    return <div>Loading...</div>;
  }

  // Safely access and extract values
  const followingValues =
    followingData.relationships_following?.flatMap(
      (item) => item.string_list_data?.map((data) => data.value) || []
    ) || [];

  const followersValues =
    followersData.relationships_following?.flatMap(
      (item) => item.string_list_data?.map((data) => data.value) || []
    ) || [];

  // Handle search input
  const handleSearchChange = (event) => {
    setSearchTerm(event.target.value);
  };

  // Compare values and filter
  const notInFollowers = followingValues
    .filter((value) => !followersValues.includes(value))
    .filter((value) => value.toLowerCase().includes(searchTerm.toLowerCase()))
    .sort((a, b) => (sortAscending ? a.localeCompare(b) : b.localeCompare(a)));

  const notInFollowing = followersValues
    .filter((value) => !followingValues.includes(value))
    .filter((value) => value.toLowerCase().includes(searchTerm.toLowerCase()))
    .sort((a, b) => (sortAscending ? a.localeCompare(b) : b.localeCompare(a)));

  return (
    <div>
      <input
        type="text"
        placeholder="Search..."
        value={searchTerm}
        onChange={handleSearchChange}
      />
      <button onClick={() => setSortAscending(!sortAscending)}>
        Sort {sortAscending ? "Descending" : "Ascending"}
      </button>

      <h2>Not in Followers</h2>
      <ul>
        {notInFollowers.map((value, index) => (
          <li key={index}>
            <a
              href={`https://www.instagram.com/${value}`}
              target="_blank"
              rel="noopener noreferrer"
            >
              {value}
            </a>
          </li>
        ))}
      </ul>

      <h2>Not in Following</h2>
      <ul>
        {notInFollowing.map((value, index) => (
          <li key={index}>
            <a
              href={`https://www.instagram.com/${value}`}
              target="_blank"
              rel="noopener noreferrer"
            >
              {value}
            </a>
          </li>
        ))}
      </ul>
    </div>
  );
};

export default Friends;
